import React from 'react';
import './Login.css';


function Login(){
    return (
        <div className="login-wrapper">
      <div className="logo">
        <svg width="60" height="60" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M39.6889 2V42H2M39.6889 2L37.3778 8.42857M39.6889 2L42 8.42857" stroke="#01A274" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round"/>
          <path d="M11.7297 32.6977L18.8898 23.8517L27.9709 32.6977L32.6861 13.1628M32.6861 13.1628L27.9709 17.9544M32.6861 13.1628L34.4324 19.4287" stroke="#01A274" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round"/>
          </svg>

        <h1>Фактор</h1>
      </div>
      <h2>Авторизация</h2>
      <form className="login" onSubmit={this.submitHandler}>
        <input 
            value={name}
            onChange={this.nameChangeHandler}
            type="text" 
            className="nickname" 
            id="name" 
            placeholder="Логин" 
            required
        />
        {nameError ? (
                        <div className='error'>Заполните поле</div>
                    ) : null}
        <input 
            value={password}
            onChange={this.passwordChangeHandler}
            type="password" 
            className="password" 
            id="password" 
            placeholder="Пароль" 
            required
        />
        {passwordError ? (
                        <div className='error'>Заполните поле</div>
                    ) : null}
        <button id="enter" className="enter" type="submit">Войти</button>
      </form>
      <div className="signin">
        У Вас ещё нет учётной записи?
        <a href="{{ url_for('auth.register') }}">Регистрация</a>   
      </div>
    </div> 
    );
}

export default Login;
