import React, { Component } from 'react';
import '../../styles/Login.css';
import { Link } from 'react-router-dom';

class LoginForm extends Component {
    state = {
        name: '',
        nameError: null,
        password: '',
        passwordError: null
    };

    nameChangeHandler = event => {
        const name = event.target.value;
        this.setState({
            name,
            nameError: !name
        });
    };

    passwordChangeHandler = event => {
        const password = event.target.value;
        this.setState({
            password,
            passwordError: !password
        });
    };

    submitHandler = event => {
        event.preventDefault();

        const { name, password } = this.state;

        if (name && password) {
            this.setState({
                name: '',
                nameError: false,
                password: '',
                passwordError: false
            });
            console.log(name, password);
            fetch("http://127.0.0.1:5000/login/",{
                credentials:'include',
                method:'POST',
                body:JSON.stringify({"login":name, "password":password})
            })
            return;
        }

        this.setState({
            nameError: !name,
            passwordError: !password
        });
    };

    render() {
        const { name, nameError, password, passwordError } = this.state;
    return(
        <form className="login" onSubmit={this.submitHandler}>
        <input 
            value={name}
            onChange={this.nameChangeHandler}
            type="text" 
            className="nickname" 
            id="name" 
            placeholder="Логин" 
            required
        />
        {nameError ? (
                        <div className='error'>! Заполните поле</div>
                    ) : null}
        <input 
            value={password}
            onChange={this.passwordChangeHandler}
            type="password" 
            className="password" 
            id="password" 
            placeholder="Пароль" 
            required
        />
        {passwordError ? (
                        <div className='error'>! Заполните поле</div>
                    ) : null}
        <Link id="enter" className="enter">Войти</Link>
      </form>
    );   
}
}

export default LoginForm;
